import React, { useContext } from "react";
import { GlobalContext } from "../../../services/AppContext";
import CommonFuntions from "../../../services/CommonFunctions";
import { updateSettings } from "../../../services/HTTPContext";

const SideMenuSettings = () => {
  const {
    PublishTemplate,
    AutoFill,
    ClearCV,
    UpdateTemplateVersion,
    UnpublishTemplate,
  } = CommonFuntions();

  const { updateIndex, updateSettingsModal, data } = useContext(GlobalContext);
  const publish = () => {
    PublishTemplate();
    updateSettings();
  };

  const unpublish = () => {
    UnpublishTemplate();
    updateSettings();
  };
  return (
    <div className="flex  justify-start items-center">
      <div className="flex-col w-full divide-y-2 gap-x-2 h-full justify-end items-center">
        <div className="flex items-center">
          <img src="logo.png" alt="" className="w-1/4 h-1/4" />
          <div className="flex w-full justify-center text-white text-2xl">
            Hi, {data.userDetails.username}
          </div>
        </div>
        <div
          onClick={() => AutoFill()}
          className="cursor-pointer hover:bg-gray-500 text-white p-3  bg-indigo-500"
        >
          AutoFill
        </div>

        {data.userSettings.personal_details.text !== "" && (
          <div className="cursor-pointer">
            <button
              onClick={() => ClearCV()}
              className="rounded text-white p-3  bg-indigo-500"
            >
              Clear
            </button>
          </div>
        )}
        <div className="cursor-pointer">
          <select
            defaultValue={data.userSettings.templateIndex}
            onChange={(e) => UpdateTemplateVersion(parseInt(e.target.value))}
            className="bg-indigo-500 p-3 text-white cursor-pointer"
          >
            <option className="cursor-pointer" value={0}>
              Template 1
            </option>
            <option className="cursor-pointer" value={1}>
              Template 2
            </option>
            <option className="cursor-pointer" value={2}>
              Template 3
            </option>
          </select>
        </div>

        {data.userSettings.personal_details.text !== "" && (
          <div className="cursor-pointer">
            <button
              onClick={() => updateSettings()}
              className="rounded text-white w-2/12 p-3 bg-indigo-500"
            >
              Save
            </button>
          </div>
        )}

        {data.userSettings.contact.sections.length !== 0 &&
          !data.userSettings.published && (
            <div className="cursor-pointer">
              <button
                onClick={() => publish()}
                className="rounded cursor-pointer text-white p-3  bg-indigo-500"
              >
                Publish CV
              </button>
            </div>
          )}

        {data.userSettings.contact.sections.length !== 0 &&
          data.userSettings.published && (
            <button
              onClick={() => unpublish()}
              className="rounded cursor-pointer text-white p-3  bg-indigo-500"
            >
              UnPublish CV
            </button>
          )}
      </div>
    </div>
  );
};
export default SideMenuSettings;
