import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../../services/AppContext";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);
  const modifyFontSize = (status: string) => {
    if (status === "up") {
      setFontSizeIndex(fontSizeIndex + 1);
    } else {
      setFontSizeIndex(fontSizeIndex - 1);
    }
  };
  const { data, updatePersonalDetails, updateIndex } =
    useContext(GlobalContext);
  console.log(data);

  let [settings, setSettings] = useState(data[data.sectionIndex].settings);
  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("up")}
            value="+"
            disabled={fontSizeIndex === fontSizeArray.length - 1}
          />

          <div>
            <input className={"w-40 " + settings.fontSize} value="Font Size" />
          </div>
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("down")}
            value="-"
            disabled={fontSizeIndex === 0}
          />
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
