import classNames from "classnames";
import React, { useContext } from "react";
import { GlobalContext } from "../../services/AppContext";
import CommonFuntions from "../CommonFunctions";

const Template = () => {
  const { data, updateData, updateIndex, sectionIndex } =
    useContext(GlobalContext);

  const { CheckSections, CheckProjectsStatus, CheckLanguagesStatus } =
    CommonFuntions();

  const updateTemplateIndex = (condition: boolean, sectionName: string) => {
    if (condition) {
      updateIndex(sectionName);
    }
  };

  return (
    <div className="flex-col justify-center items-start p-2 z-0 relative">
      <div
        onClick={() => updateIndex("personal_details")}
        className={classNames("flex-col p-3 cursor-pointer rounded", {
          "hover:bg-gray-200": sectionIndex !== "personal_details",
          "bg-gray-200": sectionIndex === "personal_details",
        })}
      >
        <div className={"flex items-center "}>
          <span className="">Personal Profile</span>
        </div>
      </div>

      <div
        onClick={() =>
          updateTemplateIndex(data.personal_details.text !== "", "education")
        }
        className={classNames("flex-col p-3 cursor-pointer rounded", {
          "hover:bg-gray-200":
            sectionIndex !== "education" && data.personal_details.text !== "",
          "bg-gray-200": sectionIndex === "education",
          "text-gray-500": data.personal_details.text === "",
        })}
      >
        <div className={"flex items-center "}>
          <span className="">Education</span>
        </div>
      </div>

      <div
        onClick={() =>
          updateTemplateIndex(
            CheckSections('education'),
            "experience"
          )
        }
        className={classNames("flex-col p-3 cursor-pointer rounded", {
          "hover:bg-gray-200":
            sectionIndex !== "experience" &&
            CheckSections('education'),
          "bg-gray-200": sectionIndex === "experience",
          "text-gray-500": CheckSections('education') === false,
        })}
      >
        <div className={"flex "}>
          <span className="">Experience</span>
        </div>
      </div>

      <div
        onClick={() =>
          updateTemplateIndex(data.experience.sections.length !== 0, "projects")
        }
        className={classNames("flex-col p-3 cursor-pointer rounded", {
          "hover:bg-gray-200":
            sectionIndex !== "projects" &&
            data.experience.sections.length !== 0,
          "bg-gray-200": sectionIndex === "projects",
          "text-gray-500": data.experience.sections.length === 0,
        })}
      >
        <div className={"flex "}>
          <span className="">Projects</span>
        </div>
      </div>

      <div
        onClick={() =>
          updateTemplateIndex(data.projects.sections.length !== 0, "languages")
        }
        className={classNames("flex-col p-3 cursor-pointer rounded", {
          "hover:bg-gray-200":
            sectionIndex !== "languages" && data.projects.sections.length !== 0,
          "bg-gray-200": sectionIndex === "languages",
          "text-gray-500": data.projects.sections.length === 0,
        })}
      >
        <div className={"flex "}>
          <span className="">Languages</span>
        </div>
      </div>

      <div
        onClick={() =>
          updateTemplateIndex(data.languages.sections.length, "contact")
        }
        className={classNames("flex-col p-3 cursor-pointer rounded", {
          "hover:bg-gray-200":
            sectionIndex !== "contact" && data.languages.sections.length !== 0,
          "bg-gray-200": sectionIndex === "contact",
          "text-gray-500": data.languages.sections.length === 0,
        })}
      >
        <div className={"flex "}>
          <span className="">Contact</span>
        </div>
      </div>
    </div>
  );
};

export default Template;
