import React from "react";
import { useContext } from "react";
import { useState } from "react";
import { useEffect } from "react";
import { Link } from "react-router-dom";
import { GlobalContext } from "../../services/AppContext";
import CommonFuntions from "../../services/CommonFunctions";
import { getUserTemplates } from "../../services/HTTPContext";
import Modal from "../Modal";
import Builder1 from "../templates/Builder1";
import Builder2 from "../templates/Builder2";
import Builder3 from "../templates/Builder3";

const DashboardBody = () => {
  const { InitializeUser } = CommonFuntions();
  InitializeUser();
  const { data } = useContext(GlobalContext);
  const [userTemplates, setUserTemplates] = useState([]);
  useEffect(() => {
    getUserTemplates(data.userDetails.id).then((response) => {
      setUserTemplates(response);
    });
  });
  const [visible, setVisible] = useState(false);
  const [modalTemplate, setModalTemplate] = useState("");

  const trClicked = (index: string) => {
    setModalTemplate(index);
    setVisible(true);
  };

  const ModalHeader = (
    <div className="flex justify-center">"Template {modalTemplate}</div>
  );
  const ModalBody = (
    <div>
      {modalTemplate === "0" ? (
        <Builder1 />
      ) : modalTemplate === "1" ? (
        <Builder2 />
      ) : (
        <Builder3 />
      )}
    </div>
  );

  return (
    <div>
      <Modal
        header={ModalHeader}
        body={ModalBody}
        visible={visible}
        onHide={setVisible(false)}
      />

      <div className="flex justify-center">
        {userTemplates.length > 0 ? (
          <table className="border-separate border border-indigo-500 w-full">
            <thead>
              <tr>
                <th className="border border-indigo-500 text-center">
                  Template Name
                </th>
                <th className="border border-indigo-500 text-center">
                  Template Index
                </th>

                <th
                  className="border border-indigo-500 text-center "
                  rowSpan={2}
                >
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>
              {userTemplates.map((template: any, index: number) => {
                return (
                  <tr
                    onClick={() => trClicked(template.templateIndex)}
                    className="hover:bg-gray-500 cursor-pointer"
                    key={index}
                  >
                    <td className="border border-indigo-500 ">
                      {template.templateName}
                    </td>
                    <td className="border border-indigo-500 text-center">
                      Template {parseInt(template.templateIndex) + 1}
                    </td>

                    <td className="border border-indigo-500">
                      <div className="flex gap-x-2">
                        <button className="p-3 w-1/2 bg-indigo-500 text-white">
                          Edit
                        </button>
                        <button className="p-3 w-1/2 bg-red-500 text-white">
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : (
          <div className="flex-col">
            <div className="text-center">No CVs Found</div>
            <div className="text-center">
              <Link to="/home" className="no-underline text-white">
                <button className="bg-indigo-500 text-white p-3">
                  Create CV
                </button>
              </Link>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
export default DashboardBody;
