import { ReactComponentElement } from "react";
import { useState } from "react";
import { Link } from "react-router-dom";
import DataService from "./services/data.service";
import Template0 from "./components/templates/template-0";
import { Templates } from "./constants";

const App = () => {
  const [templateComponent, setTemplateComponent] = useState(Template0);
  const [visible, isVisible] = useState(false);
  const showModal = (templateIndex: number) => {
    setTemplateComponent(Templates[templateIndex]);
    isVisible(true);
  };
  return (
    <div className="bg-indigo-400 h-screen ">
      <div className="p-5 relative flex-col flex justify-center z-0">
        <div className="text-center">
          <span>Choose a Template</span>
        </div>
        <div className="flex flex-wrap gap-x-3 p-5">
          <div
            onClick={() => showModal(0)}
            className="w-48 bg-gray-500 h-48 rounded flex-col flex items-center justify-center cursor-pointer"
          >
            <span>Template 1</span>
          </div>
          <div
            onClick={() => showModal(0)}
            className="w-48 bg-gray-500 h-48 rounded flex-col flex items-center justify-center cursor-pointer"
          >
            <span>Template 2</span>
          </div>
        </div>
        {visible && (
          <div className="z-1 w-screen h-screen top-1/2 flex justify-center ">
            <div className="bg-white flex-col w-3/4 h-3/4">
              <div onClick={() => isVisible(false)} className="float-right cursor-pointer">
                X
              </div>
            {templateComponent}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
