import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../services/AppContext";
import CommonFuntions from "./CommonFunctions";
import { Button, Modal } from "react-bootstrap";

const MyModal = () => {
  const { data, updateData, updateIndex, sectionIndex } =
    useContext(GlobalContext);
  const {
    AddEducation,
    ModifyText,
    UpdateFromSection,
    UpdateToSection,
    UpdateTextSection,
    AddExperience,
    AddLanguage,
    UpdateLanguage,
    AddProject,
    UpdateProjectDescription,
    UpdateProjectName,
    DeleteSection,
  } = CommonFuntions();
  //console.log(data[sectionIndex].sections)
  const [visible, isVisible] = useState(sectionIndex !== "");
  const modifyVisibility = () => {
    isVisible(false);
    updateIndex("");
  };
  useEffect(() => {
    if (sectionIndex !== "") {
      isVisible(true);
    }
  }, [sectionIndex]);
  return (
    <Modal show={visible} onHide={() => modifyVisibility()}>
      <Modal.Header closeButton>
        {sectionIndex.toUpperCase().replace("_", " ")}
      </Modal.Header>
      <Modal.Body>
        <div className="">
          {sectionIndex === "personal_details" ? (
            <div className="flex-col">
              <div className="flex justify-center">
                <textarea
                  className={"flex h-52 w-full "}
                  onChange={(e) => ModifyText(e)}
                  placeholder="Type your profile"
                  value={data[sectionIndex].text}
                />
              </div>
            </div>
          ) : sectionIndex === "education" ? (
            <div className="flex-col">
              <div className="flex justify-center">
                <button
                  onClick={() => AddEducation()}
                  className="rounded h-12 w-full p-2 bg-indigo-500"
                >
                  Add Education
                </button>
              </div>
              <br />
              <div className="flex-wrap justify-center flex items-center">
                {data[sectionIndex].sections.map((section: any, index: any) => {
                  console.log(data[sectionIndex].sections);
                  return (
                    <div className="flex-col gap-y-6 pl-2 pt-5">
                      <div className="flex justify-center">
                        <span>Education {index + 1}</span>
                      </div>
                      <div className="border-2 p-2">
                        <div className="flex gap-x-3">
                          <div>
                            <input
                              onChange={(e) => UpdateFromSection(e, index)}
                              className="w-32 p-2 rounded"
                              placeholder="from"
                              value={section.from}
                            />
                          </div>
                          <div>
                            <input
                              className="w-32 p-2 rounded"
                              placeholder="To"
                              onChange={(e) => UpdateToSection(e, index)}
                              value={section.to}
                            />
                          </div>
                        </div>
                        <br />
                        <div className="flex justify-center">
                          <textarea
                            className="rounded"
                            placeholder="Education"
                            onChange={(e) => UpdateTextSection(e, index)}
                            value={section.text}
                          />
                        </div>
                      </div>
                      <div className="flex justify-center">
                        <button
                          onClick={() => DeleteSection(index)}
                          className="p-3 w-full bg-red-500"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          ) : sectionIndex === "experience" ? (
            <div className="flex-col">
              <div className="flex justify-center">
                <button
                  onClick={() => AddExperience()}
                  className="rounded w-full h-12 p-2 bg-indigo-500"
                >
                  Add Experience
                </button>
              </div>
              <br />
              <div className="flex-wrap justify-center flex items-center">
                {data[sectionIndex].sections.map((section: any, index: any) => {
                  return (
                    <div className="flex-col gap-y-6 pl-2 pt-5">
                      <div className="flex justify-center">
                        <span>Experience {index + 1}</span>
                      </div>
                      <div className="border-2 p-2">
                        <div className="flex gap-x-3">
                          <div>
                            <input
                              onChange={(e) => UpdateFromSection(e, index)}
                              className="w-32 p-2 rounded"
                              placeholder="from"
                              value={section.from}
                            />
                          </div>
                          <div>
                            <input
                              className="w-32 p-2 rounded"
                              placeholder="To"
                              onChange={(e) => UpdateToSection(e, index)}
                              value={section.to}
                            />
                          </div>
                        </div>
                        <br />
                        <div className="flex justify-center">
                          <textarea
                            className="rounded"
                            placeholder="Experience"
                            onChange={(e) => UpdateTextSection(e, index)}
                            value={section.text}
                          />
                        </div>
                      </div>
                      <div className="flex justify-center">
                        <button
                          onClick={() => DeleteSection(index)}
                          className="p-3 w-full bg-red-500"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          ) : sectionIndex === "languages" ? (
            <div className="flex-col">
              <div className="flex justify-center">
                <button
                  onClick={() => AddLanguage()}
                  className="rounded w-full h-12 p-2 bg-indigo-500"
                >
                  Add Language
                </button>
              </div>
              {data[sectionIndex].sections.map((text: any, index: any) => {
                return (
                  <div className="flex-col gap-y-3">
                    <div className="flex">
                      <div>
                        <input
                          onChange={(e) => UpdateLanguage(e, index)}
                          className="p-2"
                          value={text}
                          placeholder={"Language " + (index + 1)}
                        />
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          ) : sectionIndex === "projects" ? (
            <div className="flex-col gap-y-2">
              <div className="flex-col flex justify-center">
                <input
                  placeholder="Project Title"
                  onChange={(e) => ModifyText(e)}
                />{" "}
                <br />
                <button
                  onClick={() => AddProject()}
                  className="rounded h-12 p-2 bg-indigo-500"
                >
                  Add Project
                </button>
              </div>

              {data[sectionIndex].sections.map((section: any, index: any) => {
                return (
                  <div className="flex-col gap-y-3">
                    <div>
                      <input
                        onChange={(e) => UpdateProjectName(e, index)}
                        value={section.projectName}
                      ></input>
                    </div>
                    <div>
                      <textarea
                        className="w-full"
                        placeholder={"Project " + (index + 1)}
                        onChange={(e) => UpdateProjectDescription(e, index)}
                      >
                        {section.projectDescription}
                      </textarea>
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <></>
          )}
        </div>
      </Modal.Body>
    </Modal>
  );
};
export default MyModal;
