import React, { createContext, useReducer } from "react";
import AppReducer from "../services/AppReducer";

const initialState = {
  data: {
    personal_details: {
      text: "Its mohammad",
      settings: {
        fontSize: "text-lg",
        textPosition: "justify-start",
        textColor: "#000000",
      },
    },

    education: {
      sections: [
        {
          from: "2020-10-10",
          to: "2021-2-2",
          text: "AUB",
        },
      ],
      settings: {
        fontSize: "text-lg",
        textPosition: "justify-start",
        textColor: "#000000",
      },
    },

    experience: {
      sections: [
        {
          from: "2020-10-10",
          to: "2021-2-2",
          text: "AUB",
        },
      ],
      settings: {
        fontSize: "text-lg",
        textPosition: "justify-start",
        textColor: "#000000",
      },
    },

    projects: {
      text: "Test Project",
      sections: [
        {
          projectName: "ProjectName",
          projectDescription: "projectDescription",
        },
      ],
      settings: {
        fontSize: "text-lg",
        textPosition: "justify-start",
        textColor: "#000000",
      },
    },
    languages: {
      sections: ["English, Arabic"],
      settings: {
        fontSize: "text-lg",
        textPosition: "justify-start",
        textColor: "#000000",
      },
    },
    contact: {
      sections: [
        {
          title: "",
          details: "mohammadalialloul@gmail.com",
        },
        {
          title: "",
          details: "mohammadalialloul@gmail.com",
        },
      ],
      settings: {
        fontSize: "text-lg",
        textPosition: "justify-start",
        textColor: "#000000",
      },
      users: {
        details: [
          {
            uid: "3456gghs22ssqaf43211",
            templateIndex: 0,
          },
          {
            uid: "467888764534232222",
            templateIndex: 1,
          },
          {
            uid: "234fgfrogjorj444",
            templateIndex: 2,
          },
        ],
      },
    },

    sectionIndex: "",
    settingsModal: false,
    templateIndex: 1,
  },
} as {
  data: any;
  updateData: (item: any) => void;
  updateIndex: (item: string) => void;
  updateSettingsModal: () => void;
  updateTempIndex: (item: number) => void;
  sectionIndex: string;
  settingsModal: boolean;
  templateIndex: number;
};

export const GlobalContext = createContext(initialState);

const GlobalProvider = ({ ...props }: any) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);
  const updateData = (item: any) => {
    dispatch({
      type: "UPDATE_DATA",
      payload: item,
    });
  };
  const updateIndex = (item: any) => {
    dispatch({
      type: "UPDATE_SECTION_INDEX",
      payload: item,
    });
  };
  const updateTempIndex = (item: number) => {
    dispatch({
      type: "UPDATE_TEMPLATE_INDEX",
      payload: item,
    });
  };
  const updateSettingsModal = () => {
    dispatch({
      type: "UPDATE_SETTINGS_MODAL",
      payload: !state.data.settingsModal,
    });
  };

  return (
    <GlobalContext.Provider
      value={{
        data: state.data,
        updateData,
        updateIndex,
        updateSettingsModal,
        updateTempIndex,
        sectionIndex: state.data.sectionIndex,
        settingsModal: state.data.settingsModal,
        templateIndex: state.data.templateIndex,
      }}
    >
      {props.children}
    </GlobalContext.Provider>
  );
};
export default GlobalProvider;
