import React from "react";
import { useState } from "react";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);
  const modifyFontSize = (status: string) => {
    if (status === "up") {
      setFontSizeIndex(fontSizeIndex + 1);
    } else {
      setFontSizeIndex(fontSizeIndex - 1);
    }
  };
  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("up")}
            value="+"
            onChange={() => null}
            disabled={fontSizeIndex === fontSizeArray.length - 1}
          />

          <div>
            <input
              className={"w-40 " + fontSizeArray[fontSizeIndex]}
              value="Font Size"
              onChange={() => null}
            />
          </div>
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("down")}
            value="-"
            onChange={() => null}
            disabled={fontSizeIndex === 0}
          />
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
