import React, { useContext } from "react";
import { useState } from "react";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
} from "recoil";
import { GlobalContext } from "../../services/AppContext";

const Template = () => {
  const { data, updatePersonalDetails } = useContext(GlobalContext);

  const modifyPersonalProfileText = (event: any) => {
    let tempData = data;
    tempData.personal_details.text = event.target.value;
    updatePersonalDetails(tempData);
  };

  return (
    <div className="flex-col justify-center items-start p-2">
      <div className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded">
        <div className={"flex "}>
          <span className="">Personal Profile</span>
        </div>
        <div>
          <textarea
            className={"flex w-full h-52 "}
            onChange={(e) => modifyPersonalProfileText(e)}
          />
        </div>
      </div>
    </div>
  );
};

export default Template;
