import React, { useContext } from "react";
import { useState } from "react";
import { Redirect, useHistory } from "react-router-dom";
import { GlobalContext } from "../services/AppContext";
import { addUser, getUsers, getUserInfo } from "../services/HTTPContext";
import CommonFuntions from "../services/CommonFunctions";

const SignIn = ({ ...props }) => {
  let { data, updateData, updateUserSettings, updateUserDetails } =
    useContext(GlobalContext);

  const { StoreUserInfo, GetUserInfo } = CommonFuntions();
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [messageError, setMessageError] = useState("");
  const signIn = () => {
    if (userName === "" || password === "") {
      setMessageError("Please fill all fields");
    } else {
      getUsers().then((response) => {
        let list = response.filter(
          (user: any) =>
            user.username === userName && user.password === password
        );
        if (list.length !== 0) {
          getUserInfo(list[0].id)
            .then((info) => {
              StoreUserInfo(info);

            })
            .then((final) => {
              props.history.push("/home");
            });
        } else {
          setMessageError("User Not Found");
        }
      });
    }
  };

  return (
    <div>
      <div className="grid grid-cols-12 justify-between items-center">
        <div className="col-span-6">
          <img src="logo.png" alt="" className="w-2/12" />
        </div>
        <div className="col-span-6 flex justify-end gap-x-0.5">
          <button className="rounded-full text-white p-3  w-2/12 bg-indigo-500">
            SignUp
          </button>
          <button className="rounded-full text-white p-3  w-2/12 bg-indigo-500">
            SignIn
          </button>
        </div>
      </div>
      <div className=" rounded flex justify-center items-center">
        <div className="flex-col">
          <div className="text-red-500">{messageError}</div>
          <div>
            <input
              className="rounded p-3 bg-white border-b-2"
              placeholder="UserName"
              type="text"
              onChange={(e) => setUserName(e.target.value)}
            />
          </div>
          <div>
            <input
              className="rounded p-3 bg-white border-b-2"
              placeholder="Password"
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <div className="p-3">
            <button
              onClick={() => signIn()}
              className="rounded-full text-white p-3 w-full  bg-indigo-500"
            >
              Submit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignIn;
