import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../../services/AppContext";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);
  const modifyFontSize = (status: string) => {
    if (status === "up") {
      setFontSizeIndex(fontSizeIndex + 1);
    } else {
      setFontSizeIndex(fontSizeIndex - 1);
    }
  };
  const { data, updatePersonalDetails, updateIndex, sectionIndex } =
    useContext(GlobalContext);
  const tempSetttings = {
    fontSize: "text-sm",
  };
  let [settings, setSettings] = useState(tempSetttings);
  console.log(data);
  useEffect(() => {
    Object.keys(data).map((item, index) => {
      let list = null;
      if (index === sectionIndex) {
        list = data[item].settings;
      }
      setSettings(list);
    });
  }, [sectionIndex]);
  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("up")}
            value="+"
            onChange={() => null}
            disabled={fontSizeIndex === fontSizeArray.length - 1}
          />

          <div>
            <input
              className={"w-40 " + settings.fontSize}
              value="Font Size"
              onChange={() => null}
            />
          </div>
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("down")}
            value="-"
            onChange={() => null}
            disabled={fontSizeIndex === 0}
          />
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
