import React, { useContext, useState } from "react";
import Education from "./components/Education";
import Experience from "./components/Experience";
import Languages from "./components/Languages";
import Modal from "./components/Modal";
import PersonalProfile from "./components/PersonalProfile";
import Projects from "./components/Projects";
import Configurations from "./components/templates/Configurations";
import Settings from "./components/templates/Settings";
import Template from "./components/templates/Template";
import { GlobalContext } from "./services/AppContext";
import { ExportToWord } from "./services/ExportToWord";

const App = () => {
  const { Header, Body } = Configurations();
  const { SettingsHeader, SettingsBody } = Settings();
  const { data, updateData, updateIndex, sectionIndex, settingsModal } =
    useContext(GlobalContext);
  return (
    <div className="grid grid-cols-12 h-screen">
      <div className="col-span-3">
        <div className="flex items-center">
          <img src="logo.png" className="w-1/4 h-1/2" alt="" />
          <span>v 1.0.0</span>
        </div>
        <div className="bg-indigo-500 h-full">
          <Template />
        </div>
      </div>

      <div className="col-span-9">
        <div className="flex items-center">
          <Modal Header={Header} Body={Body} visible={sectionIndex !== ""} />
          <Modal
            Header={SettingsHeader}
            Body={SettingsBody}
            visible={settingsModal}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
