import React from "react";
import { v4 as uuidv4 } from "uuid";

export const addUser = async (username: string, password: string) => {
  const requestOptions = {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      id: uuidv4(),
      username: username,
      password: password,
    }),
  };
  return await fetch("http://localhost:8000/users/", requestOptions).then(
    (response) => response.json()
  );
};

export const addUserSettings = async (id: string) => {
  const requestOptions = {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      templateIndex: 1,
      personal_details: {
        text: "",
        settings: {
          fontSize: "text-lg",
          textPosition: "justify-start",
          textColor: "#000000",
        },
      },
      education: {
        sections: [],
        settings: {
          fontSize: "text-lg",
          textPosition: "justify-start",
          textColor: "#000000",
        },
      },
      experience: {
        sections: [],
        settings: {
          fontSize: "text-lg",
          textPosition: "justify-start",
          textColor: "#000000",
        },
      },
      projects: {
        text: "",
        sections: [],
        settings: {
          fontSize: "text-lg",
          textPosition: "justify-start",
          textColor: "#000000",
        },
      },
      languages: {
        sections: [],
        settings: {
          fontSize: "text-lg",
          textPosition: "justify-start",
          textColor: "#000000",
        },
      },
      contact: {
        sections: [],
        settings: {
          fontSize: "text-lg",
          textPosition: "justify-start",
          textColor: "#000000",
        },
      },
    }),
  };
  return await fetch("http://localhost:8000/settings/", requestOptions).then(
    (response) => response.json()
  );
};

export const getUsers = async () => {
  return await fetch("http://localhost:8000/users").then((response) =>
    response.json()
  );
};

export const getUserDetails = async (userId: string) => {
  return await fetch("http://localhost:8000/users/" + userId).then((response) =>
    response.json()
  );
};

export const getUserSettings = async (userId: string) => {
  return await fetch("http://localhost:8000/settings/" + userId).then(
    (response) => response.json()
  );
};

export const getUserInfo = async (userId: string) => {
  let user = {
    userDetails: {},
    userSettings: {},
  };

  await getUserDetails(userId).then((response) => {
    user.userDetails = response;
  });
  await getUserSettings(userId).then((response) => {
    user.userSettings = response;
  });
  return user;
};
