import React, { useContext } from "react";
import Settings from "./components/templates/Settings";
import Template from "./components/templates/Template";
import { useState } from "react";
import { configData } from "./constants";
import { GlobalContext } from "./services/AppContext";
import { ExportToWord } from "./services/ExportToWord";

const App = () => {
  const { data } = useContext(GlobalContext);

  return (
    <div className="flex-col">
      <Settings />
      <button
        onClick={() => console.log(document.getElementById("exportToWord"))}
      >
        Export
      </button>
      <div className="flex">
        <div className="h-screen w-1/4 bg-gray-500">
          <Template />
        </div>

        <div className="w-3/4 bg-green-200" id="exportToWord">
          {data.personal_details.text !== "" && (
            <div className="flex-col">
              <div>Personal Profile</div>
              <div
                style={{ color: data.personal_details.settings.textColor }}
                className={
                  data.personal_details.settings.fontSize +
                  " flex " +
                  data.personal_details.settings.textPosition
                }
              >
                {data.personal_details.text}
              </div>
            </div>
          )}

          {data.experience.sections.length !== 0 && (
            <div className="flex-col">
              <div>Experience</div>

              {data.experience.sections.map((section: any, index: number) => {
                return (
                  <div
                    style={{ color: data.experience.settings.textColor }}
                    className={
                      data.experience.settings.fontSize +
                      " flex " +
                      data.experience.settings.textPosition
                    }
                  >
                    <div className="flex-col gap-y-3">
                      <div className="flex">
                        <div>
                          <span className="">{section.from}</span>
                          <span>-</span>
                          <span className="">{section.to}</span>
                        </div>
                      </div>
                      <div>
                        <span className="">{section.text}</span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}

          {data.education.sections.length !== 0 && (
            <div className="flex-col">
              <div>Education</div>

              {data.education.sections.map((section: any, index: number) => {
                return (
                  <div
                    style={{ color: data.education.settings.textColor }}
                    className={
                      data.education.settings.fontSize +
                      " flex " +
                      data.education.settings.textPosition
                    }
                  >
                    <div className="flex-col gap-y-3">
                      <div className="flex">
                        <div>
                          <span className="">{section.from}</span>
                          <span>-</span>
                          <span className="">{section.to}</span>
                        </div>
                      </div>
                      <div>
                        <span className="">{section.text}</span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}

          {data.projects.text !== "" && (
            <div className="flex-col">
              <div>Projects</div>
              <div>{data.projects.text}</div>
              {data.projects.sections.map((section: any, index: number) => {
                return (
                  <div
                    style={{ color: data.projects.settings.textColor }}
                    className={
                      data.projects.settings.fontSize +
                      " flex " +
                      data.projects.settings.textPosition
                    }
                  >
                    <div className="flex-col gap-y-3">
                      <div className="flex-col">
                        <span className="">{section.projectName}</span>

                        <span className="">{section.projectDescription}</span>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}

          {data.languages.sections.length !== 0 && (
            <div className="flex-col">
              <div>Languages</div>
              {data.languages.sections.map((text: string, index: number) => {
                return (
                  <div
                    style={{ color: data.projects.settings.textColor }}
                    className={
                      data.projects.settings.fontSize +
                      " flex " +
                      data.projects.settings.textPosition
                    }
                  >
                    <div className="flex-col gap-y-3">
                      <div className="flex-col">{text}</div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
