import React, { useContext } from "react";
import Modal from "../components/Modal";
import Configurations from "../components/templates/properties/Configurations";
import Settings from "../components/templates/properties/Settings";
import SideMenu from "../components/templates/properties/SideMenu";
import Wrapper from "../components/Wrapper";
import { GlobalContext } from "../services/AppContext";
import CommonFuntions from "../services/CommonFunctions";
import { updateSettings } from "../services/HTTPContext";

const Builder = () => {
  const { Header, Body } = Configurations();
  const { PublishTemplate } = CommonFuntions();
  const { SettingsHeader, SettingsBody } = Settings();
  const { updateIndex, updateSettingsModal, data } = useContext(GlobalContext);
  console.log(data);
  const modifyTemplateVisibility = () => {
    updateIndex("");
  };
  const modifySettingsVisibility = () => {
    updateSettingsModal();
  };
  const publish = () => {
    PublishTemplate();
    updateSettings();
  };

  return (
    <>
      <div className="grid grid-cols-6">
        <div className="col-span-3">
          <div className="flex items-center">
            <img src="logo.png" alt="" className="w-2/12" />
          </div>
        </div>
        <div className="col-span-3">
          <div className="flex h-full justify-end items-center">
            <button
              onClick={() => updateSettings()}
              className="rounded-full text-white w-2/12 p-3 bg-indigo-500"
            >
              Save
            </button>
            <button
              onClick={() => publish()}
              className="rounded-full text-white p-3 w-2/12 bg-indigo-500"
            >
              Publish CV
            </button>
          </div>
        </div>
      </div>
      {data.userSettings.published && (
        <div className="bg-green-500 rounded">
          <span>
            You can view your CV on{" "}
            <a target="_blank" href={"localhost:8000/template/" + data.userDetails.id}>
              localhost:8000/template/{data.userDetails.id}
            </a>
          </span>
        </div>
      )}

      <div className="grid grid-cols-12 h-screen">
        <div className="bg-indigo-500 col-span-3 h-full">
          <SideMenu />
        </div>

        <div className="col-span-9 p-3">
          <Wrapper />

          <div className="flex items-center">
            <Modal
              Header={Header}
              Body={Body}
              visible={data.sectionIndex !== ""}
              onClick={modifyTemplateVisibility}
            />
            <Modal
              Header={SettingsHeader}
              Body={SettingsBody}
              visible={data.settingsModal}
              onClick={modifySettingsVisibility}
            />
          </div>
        </div>
      </div>
    </>
  );
};

export default Builder;
