import React from "react";
import Wrapper from "../components/builder/BuilderBody";
import CommonFuntions from "../services/CommonFunctions";
import TemplateSteps from "../components/builder/TemplateSteps";

import Layout from "../components/Layout";
import { hostname } from "os";

const UserProfile = () => {
  const { InitializeUser } = CommonFuntions();
  InitializeUser();
  let array = ["apibox1", "apibox2", "sitebox1"];
  const allocate = (hostName: string) => {
    var new_array: any = array.filter((item) => item.indexOf(hostName) >= 0);

    for (var i = 0; i < new_array.length; i++) {
      new_array[i] = parseInt(new_array[i].split(hostName)[1]);
    }

    var max = Math.max(...new_array);

    var new_index = -1;
    for (var i = 0; i < max; i++) {
      if (i + 1 !== new_array[i]) {
        new_index = i + 1;
        break;
      }
    }

    if (new_index === -1) {
      var temp_index = max + 1;
      while (new_index === -1) {
        if (new_array.includes(temp_index)) {
          temp_index += 1;
        } else {
          new_index = temp_index;
          break;
        }
      }
    }

    array.push(hostName + "" + new_index);
    return hostName + "" + new_index;
  };
  const deallocate = (hostName: string) => {
    var new_array: any = array.filter((item) => item.indexOf(hostName) >= 0);
    for (var i = 0; i < new_array.length; i++) {
      new_array[i] = parseInt(new_array[i].split(hostName)[1]);
    }
    console.log(new_array);

    var min = Math.min(...new_array);

    array = array.filter((host: any) => host !== hostName + "" + min);
  };
  console.log(allocate("apibox"));
  console.log(allocate("apibox"));
  deallocate("apibox");

  const header = (
    <div className="flex h-full text-2xl items-center text-white">
      <span>User Profile</span>
    </div>
  );

  return <Layout header={header} />;
};

export default UserProfile;
