import React from "react";
import { useState } from "react";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);
  const [fontSize, setFontSize] = useState(fontSizeArray[fontSizeIndex]);
  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex">
          <div onClick={() => setFontSizeIndex(fontSizeIndex + 1)}>+</div>
          <div>
            <input className={fontSize}>Font Size</input>
          </div>
          <div onClick={() => setFontSizeIndex(fontSizeIndex - 1)}>-</div>
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
