import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../../services/AppContext";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);

  const { data, updateData, updateIndex, sectionIndex } =
    useContext(GlobalContext);
  const tempSettings = {
    fontSize: "text-sm",
  };
  const [settings, setSettings] = useState(tempSettings);
  useEffect(() => {
    setSettings(data[sectionIndex].settings);
  }, [sectionIndex]);

  const increaseFontSize = () => {
    data[sectionIndex].settings.fontSize =
      fontSizeArray[fontSizeArray.indexOf(settings.fontSize + 1)];
    updateData(data);
  };
  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <input
            className={"cursor-pointer w-10 text-center"}
            value="+"
            onClick={() => increaseFontSize()}
            disabled={
              fontSizeArray.indexOf(settings.fontSize) ===
              fontSizeArray.length - 1
            }
          />

          <div>
            <input className={"w-40 " + settings.fontSize} value="Font Size" />
          </div>
          <input
            className={"cursor-pointer w-10 text-center"}
            value="-"
            disabled={fontSizeArray.indexOf(settings.fontSize) === 0}
          />
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
