    <div className="grid grid-cols-12 h-screen">
      <div className="col-span-3">
          <div className="h-1/4">
            <div className="flex items-center">
              <img src="logo.png" className="w-1/4 h-1/2" alt="" />
              <span>v 1.0.0</span>
            </div>
          </div>
          <div className="bg-indigo-500">
            <PersonalProfile />
            <Experience />
            <Education />
            <Projects />
            <Languages />
          </div>
      </div>
    </div>
  );
};

export default App;
