import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../../services/AppContext";
import { SketchPicker } from "react-color";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [visible, isVisible] = useState(false);
  const { data, updateData, updateIndex, sectionIndex } =
    useContext(GlobalContext);
  const tempSettings = {
    fontSize: "text-sm",
  };
  const [settings, setSettings] = useState(tempSettings);
  useEffect(() => {
    setSettings(data[sectionIndex].settings);
  }, [sectionIndex]);

  const increaseFontSize = () => {
    data[sectionIndex].settings.fontSize =
      fontSizeArray[fontSizeArray.indexOf(settings.fontSize) + 1];
    updateData(data);
  };
  const decreaseFontSize = () => {
    data[sectionIndex].settings.fontSize =
      fontSizeArray[fontSizeArray.indexOf(settings.fontSize) - 1];
    updateData(data);
  };
  const modifyTextPosition = (event: any) => {
    data[sectionIndex].settings.textPosition = event.target.value;
    updateData(data);
  };
  const changeTextColor = (event: any) => {
    data[sectionIndex].settings.textColor = event.hex;
    updateData(data);
  };
  return (
    <div className="flex flex-wrap h-36 gap-x-5">
      <div className="rounded bg-gray-500">
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <input
            className={"cursor-pointer w-10 text-center"}
            value="+"
            onClick={() => increaseFontSize()}
            disabled={
              fontSizeArray.indexOf(settings.fontSize) ===
                fontSizeArray.length - 1 || data[sectionIndex].text === ""
            }
            readOnly={true}
          />

          <div>
            <input className={"w-40 " + settings.fontSize} value="Font Size" />
          </div>
          <input
            className={"cursor-pointer w-10 text-center"}
            value="-"
            onClick={() => decreaseFontSize()}
            disabled={
              fontSizeArray.indexOf(settings.fontSize) === 0 ||
              data[sectionIndex].text === ""
            }
            readOnly={true}

          />
        </div>
      </div>
      <div className="rounded bg-gray-500">
        <span>Position</span>
        <div>
          <select onChange={(e) => modifyTextPosition(e)}>
            <option
              selected={
                data[sectionIndex].settings.textPosition === "justify-start"
              }
              value="justify-start"
            >
              Left
            </option>
            <option
              selected={data[sectionIndex].settings.textPosition === "justify-end"}
              value="justify-end"
            >
              Right
            </option>
            <option
              selected={
                data[sectionIndex].settings.textPosition === "justify-center"
              }
              value="justify-center"
            >
              Center
            </option>
          </select>
        </div>
      </div>

      <div className="rounded bg-gray-500">
        <div className="h-52">
          <span className="cursor-pointer" onClick={() => isVisible(!visible)}>
            Color
          </span>
          {visible && (
            <div>
              <SketchPicker
                className="h-52"
                color={data[sectionIndex].settings.textColor}
                onChange={(e: any) => changeTextColor(e)}
              />
              ColorPicker : {data[sectionIndex].settings.textColor}
            </div>
          )}
        </div>
      </div>
      <div></div>
    </div>
  );
};

export default Settings;
