import React from "react";
import { useState } from "react";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);
  const [fontSize, setFontSize] = useState("text-sm");
  const modifyFontSize = (status: string) => {
    if (status === "up") {
      setFontSizeIndex(fontSizeIndex + 1);
      setFontSize(fontSizeArray[fontSizeIndex + 1]);
    } else {
      setFontSizeIndex(fontSizeIndex - 1);
      setFontSize(fontSizeArray[fontSizeIndex - 1]);
    }
  };
  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <div className="cursor-pointer" onClick={() => modifyFontSize("up")}>
            +
          </div>
          <div>
            <input className={"w-40 " + fontSize} value="Font Size" />
          </div>
          <div
            className="cursor-pointer"
            onClick={() => setFontSizeIndex(fontSizeIndex - 1)}
          >
            -
          </div>
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
