import React, { useContext, useState } from "react";
import Contact from "./components/Contact";
import Education from "./components/Education";
import Experience from "./components/Experience";
import Languages from "./components/Languages";
import Modal from "./components/Modal";
import PersonalProfile from "./components/PersonalProfile";
import Projects from "./components/Projects";
import Builder1 from "./components/templates/builder/Builder1";
import Builder2 from "./components/templates/builder/Builder2";
import Configurations from "./components/templates/properties/Configurations";
import Settings from "./components/templates/properties/Settings";
import SideMenu from "./components/templates/properties/SideMenu";
import { GlobalContext } from "./services/AppContext";
import { ExportToWord } from "./services/ExportToWord";

const Home = () => {
  const [index, setIndex] = useState(0);
  const templatesArray = [<Builder1 />, <Builder2 />];
  const next = () => {
    if (index + 1 < templatesArray.length) {
      setIndex(index + 1);
    }
  };
  const previous = () => {
    if (index - 1 >= 0) {
      setIndex(index - 1);
    }
  };
  return (
    <>
    <div className="flex justify-center">
      <h1>Choose Your CV Template</h1>
    </div>
      <div className="grid grid-cols-12">
        <div
          onClick={() => previous()}
          className="col-span-2 cursor-pointer flex justify-center items-center"
        >
          {"<"}
        </div>

        <div className="col-span-8">{templatesArray[index]}</div>
        <div
          onClick={() => next()}
          className="col-span-2 cursor-pointer flex justify-center items-center"
        >
          {">"}
        </div>
      </div>
    </>
  );
};

export default Home;
