import React, { useContext } from "react";
import Modal from "../components/Modal";
import Configurations from "../components/templates/properties/Configurations";
import Settings from "../components/templates/properties/Settings";
import SideMenu from "../components/templates/properties/SideMenu";
import Wrapper from "../components/templates/sections/Wrapper";
import { GlobalContext } from "../services/AppContext";
import CommonFuntions from "../services/CommonFunctions";
import { updateSettings } from "../services/HTTPContext";

const Builder = () => {
  const { Header, Body } = Configurations();
  const { PublishTemplate, AutoFill, ClearCV, UpdateTemplateVersion, UnpublishTemplate } =
    CommonFuntions();
  const { SettingsHeader, SettingsBody } = Settings();
  const { updateIndex, updateSettingsModal, data } = useContext(GlobalContext);
  const modifyTemplateVisibility = () => {
    updateIndex("");
  };
  const modifySettingsVisibility = () => {
    updateSettingsModal();
  };
  const publish = () => {
    PublishTemplate();
    updateSettings();
  };

  const unpublish = () => {
    UnpublishTemplate();
    updateSettings();
  };

  return (
    <div className="pl-10 pr-10">
      <div className="grid grid-cols-6">
        <div className="col-span-3">
          <div className="flex items-center">
            <img src="logo.png" alt="" className="w-2/12" />
          </div>
        </div>
        <div className="col-span-3">
          <div className="flex gap-x-2 h-full justify-end items-center">
            <button
              onClick={() => AutoFill()}
              className="rounded text-white p-3 w-2/12 bg-indigo-500"
            >
              AutoFill
            </button>

            {data.userSettings.personal_details.text !== "" && (
              <button
                onClick={() => ClearCV()}
                className="rounded text-white p-3 w-2/12 bg-indigo-500"
              >
                Clear
              </button>
            )}

            <select
              defaultValue={data.userSettings.templateIndex}
              onChange={(e) => UpdateTemplateVersion(parseInt(e.target.value))}
              className="bg-indigo-500 p-3 text-white cursor-pointer"
            >
              <option className="cursor-pointer" value={0}>
                Template 1
              </option>
              <option className="cursor-pointer" value={1}>
                Template 2
              </option>
              <option className="cursor-pointer" value={2}>
                Template 3
              </option>
            </select>

            {data.userSettings.personal_details.text !== "" && (
              <button
                onClick={() => updateSettings()}
                className="rounded text-white w-2/12 p-3 bg-indigo-500"
              >
                Save
              </button>
            )}

            {data.userSettings.contact.sections.length !== 0 &&
              !data.userSettings.published && (
                <button
                  onClick={() => publish()}
                  className="rounded text-white p-3 w-2/12 bg-indigo-500"
                >
                  Publish CV
                </button>
              )}

            {data.userSettings.contact.sections.length !== 0 &&
              data.userSettings.published && (
                <button
                  onClick={() => unpublish()}
                  className="rounded text-white p-3 w-2/12 bg-indigo-500"
                >
                  UnPublish CV
                </button>
              )}
          </div>
        </div>
      </div>
      {data.userSettings.published && (
        <div className="bg-green-300 p-3  rounded">
          <span>
            You can view your CV on{" "}
            <a
              target="_blank"
              rel="noreferrer"
              href={"localhost:3000/template/" + data.userDetails.id}
            >
              localhost:3000/template/{data.userDetails.id}
            </a>
          </span>
        </div>
      )}
      <br />
      <div className="grid grid-cols-12 h-screen">
        <div className="bg-indigo-500 col-span-3 h-full">
          <SideMenu />
        </div>

        <div className="col-span-9 p-3">
          <Wrapper />

          <div className="flex items-center">
            <Modal
              Header={Header}
              Body={Body}
              visible={data.sectionIndex !== ""}
              onClick={modifyTemplateVisibility}
            />
            <Modal
              Header={SettingsHeader}
              Body={SettingsBody}
              visible={data.settingsModal}
              onClick={modifySettingsVisibility}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Builder;
