import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
} from "recoil";

import { templateDetails } from "../../store";

const Template = () => {
  const [details, setDetails] = useRecoilState(templateDetails);

  const modifyPersonalProfileText = (e: any) => {
    const tempDetails = details;
    tempDetails.personal_details.text = e.target.value;
    setDetails(tempDetails);
  };

  return (
    <div className="flex-col justify-center items-start p-2">
      <div className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded">
        <div
          className={"flex " + details.personal_details.settings.titlePosition}
        >
          <span className="">Personal Profile</span>
        </div>
        <div>
          <textarea
            className={
              "flex w-96 h-52 " + details.personal_details.settings.textPosition
            }
            onChange={(e) => modifyPersonalProfileText(e)}
          />
        </div>
      </div>
    </div>
  );
};

export default Template;
