import { useState } from "react";
import DataService from "./services/data.service";

const App = () => {
  const temp: any = [];
  const [tableData, setTableData] = useState(temp);
  const [loading, setLoading] = useState(true);
  const [visible, setVisible] = useState(false);
  const [response, setResponse] = useState(temp);

  const Loader = () => {
    let circleCommonClasses = "h-2.5 w-2.5 bg-current   rounded-full";
    return (
      <div className="flex">
        <div className={`${circleCommonClasses} mr-1 animate-bounce`}></div>
        <div className={`${circleCommonClasses} mr-1 animate-bounce200`}></div>
        <div className={`${circleCommonClasses} animate-bounce400`}></div>
      </div>
    );
  };

  const getData = () => {
    setVisible(true);
    setLoading(true);
    DataService.getAll("http://algotraders.pythonanywhere.com/webDev/")
      .then((response: any) => {
        setResponse(response);
        setTableData(response.data);
        setLoading(false);
      })
      .catch((e: any) => {
        console.log(e);
      });
  };

  return (
    <div className="pl-3 pr-3 flex-col p-2 gap-y-2">
      <div className="flex items-center justify-center">
        <button
          className="bg-gray-500 w-48 rounded text-white p-3"
          onClick={() => getData()}
        >
          Load Data
        </button>
      </div>
      {visible && (
        <div className="mt-2">
          {loading ? (
            <div className="h-36 flex justify-center items-center">
              <Loader />
            </div>
          ) : (
            <div className="flex justify-center">
              <table className="w-full border-2">
                <thead></thead>
                <tbody>
                  {response.data.Matrix.map((dataArray: any, i: number) => {
                    return (
                      <tr key={i}>
                        {dataArray.map((number: string, j: number) => {
                          console.log(tableData);
                          return (
                            <td className="p-2 border-2" key={i + 1 * j}>
                              {parseInt(number) /
                                parseInt(response.data.Constant + "")}
                            </td>
                          );
                        })}
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
