import React, { useContext } from "react";
import { useState } from "react";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
} from "recoil";
import { GlobalContext } from "../../services/AppContext";

const Template = () => {
  const { data, updateData, updateIndex, sectionIndex } =
    useContext(GlobalContext);
  console.log(data);
  const AddExperience = () => {
    let list = {
      from: "",
      to: "",
      text: "",
    };
    data[sectionIndex].sections.push(list);
    updateData(data);
  };

  const AddLanguage = () => {
    data[sectionIndex].sections.push("");
    updateData(data);
  };

  const AddEducation = () => {
    let list = {
      from: "",
      to: "",
      text: "",
    };
    data[sectionIndex].sections.push(list);
    updateData(data);
  };
  const AddProject = () => {
    let list = {
      projectName: "",
      projectDescription: "",
    };
    data[sectionIndex].sections.push(list);
    updateData(data);
  };

  const updateProjectName = (e: any, index: number) => {
    data["projects"].sections[index].projectName = e.target.value;
    updateData(data);
  };

  const updateProjectDescription = (e: any, index: number) => {
    data["projects"].sections[index].projectDescription = e.target.value;
    updateData(data);
  };

  const updateProjectText = (event: any) => {
    data[sectionIndex].text = event.target.value;
    updateData(data);
  };

  const modifyText = (event: any) => {
    data[sectionIndex].text = event.target.value;
    updateData(data);
  };
  const updateFromSection = (e: any, index: number) => {
    data[sectionIndex].sections[index].from = e.target.value;
    updateData(data);
  };
  const updateToSection = (e: any, index: number) => {
    data[sectionIndex].sections[index].to = e.target.value;
    updateData(data);
  };
  const updateTextSection = (e: any, index: number) => {
    data[sectionIndex].sections[index].text = e.target.value;
    updateData(data);
  };

  return (
    <div className="flex-col justify-center items-start p-2">
      <div
        onClick={() => updateIndex("personal_details")}
        className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded"
      >
        <div className={"flex "}>
          <span className="">Personal Profile</span>
        </div>
        {sectionIndex === "personal_details" && (
          <div>
            <textarea
              className={"flex w-full h-52 "}
              onChange={(e) => modifyText(e)}
            />
          </div>
        )}
      </div>

      <div
        onClick={() => updateIndex("education")}
        className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded"
      >
        <div className={"flex "}>
          <span className="">Education</span>
        </div>
        {sectionIndex === "education" && (
          <div>
            <button
              onClick={() => AddEducation()}
              className="rounded w-12 h-12"
            >
              Add
            </button>
            {data[sectionIndex].sections.map((section: any, index: any) => {
              return (
                <div className="flex-col gap-y-3">
                  <div className="flex">
                    <div>
                      <input
                        onChange={(e) => updateFromSection(e, index)}
                        className=""
                        placeholder="from"
                      />
                    </div>
                    <div>
                      <input
                        className=""
                        placeholder="To"
                        onChange={(e) => updateToSection(e, index)}
                      />
                    </div>
                  </div>
                  <div>
                    <textarea
                      className=""
                      placeholder="Experience"
                      onChange={(e) => updateTextSection(e, index)}
                    />
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div
        onClick={() => updateIndex("experience")}
        className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded"
      >
        <div className={"flex "}>
          <span className="">Experience</span>
        </div>
        {sectionIndex === "experience" && (
          <div>
            <button
              onClick={() => AddExperience()}
              className="rounded w-12 h-12"
            >
              Add
            </button>
            {data[sectionIndex].sections.map((section: any, index: any) => {
              return (
                <div className="flex-col gap-y-3">
                  <div className="flex">
                    <div>
                      <input
                        onChange={(e) => updateFromSection(e, index)}
                        className=""
                        placeholder="from"
                      />
                    </div>
                    <div>
                      <input
                        className=""
                        placeholder="To"
                        onChange={(e) => updateToSection(e, index)}
                      />
                    </div>
                  </div>
                  <div>
                    <textarea
                      className=""
                      placeholder="Experience"
                      onChange={(e) => updateTextSection(e, index)}
                    />
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>


      <div
        onClick={() => updateIndex("languages")}
        className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded"
      >
        <div className={"flex "}>
          <span className="">Languages</span>
        </div>
        {sectionIndex === "languages" && (
          <div>
            <button
              onClick={() => AddLanguage()}
              className="rounded w-12 h-12"
            >
              Add
            </button>
            {data[sectionIndex].sections.map((section: any, index: any) => {
              return (
                <div className="flex-col gap-y-3">
                  <div className="flex">
                    <div>
                      <input
                        onChange={(e) => updateFromSection(e, index)}
                        className=""
                        placeholder="from"
                      />
                    </div>
                    <div>
                      <input
                        className=""
                        placeholder="To"
                        onChange={(e) => updateToSection(e, index)}
                      />
                    </div>
                  </div>
                  <div>
                    <textarea
                      className=""
                      placeholder="Experience"
                      onChange={(e) => updateTextSection(e, index)}
                    />
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div
        onClick={() => updateIndex("projects")}
        className="flex-col hover:bg-gray-200 p-3 cursor-pointer bg-gray-500 rounded"
      >
        <div className={"flex "}>
          <span className="">Projects</span>
        </div>
        {sectionIndex === "projects" && (
          <div className="flex-col">
            <textarea onChange={(e) => updateProjectText(e)} /> <br />
            <button onClick={() => AddProject()} className="rounded w-12 h-12">
              Add
            </button>
            {data[sectionIndex].sections.map((section: any, index: any) => {
              return (
                <div className="flex-col gap-y-3">
                  <div>
                    <input
                      onChange={(e) => updateProjectName(e, index)}
                      value={section.projectName}
                    ></input>
                  </div>
                  <div>
                    <textarea
                      className=""
                      placeholder="Experience"
                      onChange={(e) => updateProjectDescription(e, index)}
                    >
                      {section.projectDescription}
                    </textarea>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default Template;
