import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../services/AppContext";
import CommonFuntions from "./CommonFunctions";
import { Button, Modal } from "react-bootstrap";

const MyModal = ({ ...props }) => {
  const { data, updateData, updateIndex, sectionIndex } =
    useContext(GlobalContext);

  const [visible, isVisible] = useState(sectionIndex !== "");
  const modifyVisibility = () => {
    isVisible(false);
    updateIndex("");
  };
  useEffect(() => {
    if (sectionIndex !== "") {
      isVisible(true);
    }
  }, [sectionIndex]);
  return (
    <Modal show={visible} onHide={() => modifyVisibility()}>
      <Modal.Header closeButton>{props.Header}</Modal.Header>
      <Modal.Body>{props.Body}</Modal.Body>
    </Modal>
  );
};
export default MyModal;
