import React, { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import { GlobalContext } from "../../services/AppContext";

const Settings = () => {
  const fontSizeArray = [
    "text-sm",
    "text-lg",
    "text-2xl",
    "text-3xl",
    "text-4xl",
  ];
  const [fontSizeIndex, setFontSizeIndex] = useState(0);

  const { data, updatePersonalDetails, updateIndex, sectionIndex } =
    useContext(GlobalContext);
  const modifyFontSize = (status: string) => {
    const index = fontSizeArray.indexOf(
      data.personal_details.settings.fontSize
    );
    if (status === "up") {
      if (data.sectionIndex === 0) {
        data.personal_details.settings.fontSize = fontSizeArray[index + 1];
        updatePersonalDetails(data);
      }
    } else {
      if (data.sectionIndex === 0) {
        data.personal_details.settings.fontSize = fontSizeArray[index - 1];
        updatePersonalDetails(data);
      }
    }
  };

  return (
    <div className="flex-col">
      <div>
        <span>Font Size</span>
        <div className="flex gap-x-3">
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("up")}
            value="+"
            disabled={fontSizeIndex === fontSizeArray.length - 1}
          />

          <div>
            <input
              className={"w-40 " + data[data.sectionIndex].settings.fontSize}
              value="Font Size"
            />
          </div>
          <input
            className={"cursor-pointer w-10 text-center"}
            onClick={() => modifyFontSize("down")}
            value="-"
            disabled={fontSizeIndex === 0}
          />
        </div>
      </div>
      <div>
        <span>Position</span>
        <div>
          <select>
            <option>Left</option>
            <option>Right</option>
            <option>Center</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Settings;
