import React from "react";
import { useContext } from "react";
import { useState } from "react";
import { Redirect, useHistory } from "react-router-dom";
import { GlobalContext } from "./services/AppContext";
import { addUser, getUsers } from "./services/HTTPContext";

const SignUp = ({ ...props }) => {
  const { data, updateData } = useContext(GlobalContext);
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [messageError, setMessageError] = useState("");
  const signUp = () => {
    if (userName === "" || password === "" || confirmPassword === "") {
      setMessageError("Please fill all fields");
    } else if (password.length > 8) {
      setMessageError("Password length should be greater than 8");
    } else if (password !== confirmPassword) {
      setMessageError("Passwords don't match");
    } else {
      getUsers().then((response) => {
        let list = response.filter((user: any) => user.username === userName);
        if (list.length === 0) {
          addUser(userName, password);

          props.history.push("/");
        } else {
          setMessageError("This username already in use. User another one");
        }
      });
    }
  };

  return (
    <div>
      <div className="grid grid-cols-12 justify-between items-center">
        <div className="col-span-6">
          <img src="logo.png" alt="" className="w-2/12" />
        </div>
        <div className="col-span-6 flex justify-end gap-x-0.5">
          <button className="rounded-full text-white p-3  w-2/12 bg-indigo-500">
            SignUp
          </button>
          <button className="rounded-full text-white p-3  w-2/12 bg-indigo-500">
            SignIn
          </button>
        </div>
      </div>
      <div className=" rounded flex justify-center items-center">
        <div className="flex-col">
          <div className="text-red-500">{messageError}</div>
          <div>
            <input
              className="rounded p-3 bg-white border-b-2"
              placeholder="UserName"
              type="text"
              onChange={(e) => setUserName(e.target.value)}
            />
          </div>
          <div>
            <input
              className="rounded p-3 bg-white border-b-2"
              placeholder="Password"
              type="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div>
            <input
              className="rounded p-3 bg-white border-b-2"
              placeholder="Confirm Password"
              type="password"
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
          </div>
          <div className="p-3">
            <button
              onClick={() => signUp()}
              className="rounded-full text-white p-3 w-full  bg-indigo-500"
            >
              Submit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
